Procedure.Verbatim:
collaborator = Mock.createMock(Collaborator);
Mock.expect(collaborator.documentAdded("New Document")).andReturn(1);
Mock.expect(collaborator.documentAdded("New Document")).andReturn(2);
Mock.replay(collaborator);

assertEquals(1, collaborator.documentAdded("New Document"));
assertEquals(2, collaborator.documentAdded("New Document"));
